@NgModule({
  imports: [
    CdkTableModule,
    CommonModule,
    MatCommonModule,
  ],
  exports: EXPORTED_DECLARATIONS,
  declarations: EXPORTED_DECLARATIONS,
})
export class MatTableModule {}
